SELECT TOP 100 * FROM MY_TABLE;

SELECT * FROM MY_TABLE;

SELECT TOP 100
    COL_A,
    COL_B
FROM MY_TABLE;

SELECT DISTINCT *
FROM MY_TABLE;

SELECT TOP 10 PERCENT * FROM MY_TABLE;

SELECT TOP 0.1 PERCENT COL_A FROM MY_TABLE;

SELECT TOP 0.1 PERCENT WITH TIES COL_A, COL_B FROM MY_TABLE ORDER BY COL_B;
